import React, { useState, useEffect } from "react";
// import renderIf from 'render-if'

import { AddLeadButton } from './components/Buttons'
import LeadsTable from './components/LeadsTable'
import AddLeadModal from './components/AddLeadModal'

import { fetchLeadsListService, addLeadService } from './HomeScreenService'

import './HomeScreenStyles.css'

export const HomeScreenView = () => {
  const [leadsList, setLeadsList] = useState([])
  const [isAddLeadModalOpen, toggleAddLeadModalOpen] = useState(false)

  const fetchLeadsList = () => {
    fetchLeadsListService()
			.then((data) => setLeadsList(data))
			.catch((error) => console.log("Error from fetchLeadsListService", error));
  }

  useEffect(() => {
		fetchLeadsList()
	}, []);

  const submitForm = async (payload) => {
    const response = await addLeadService(payload)
    console.log('response--', response)
    if (response.status === 201) {
      toggleAddLeadModalOpen(false)
      fetchLeadsList()
    }
  }

  return (
		<div className="mainContainer">
      <AddLeadModal
        toggleAddLeadModalOpen={toggleAddLeadModalOpen}
        submitForm={submitForm}
        isAddLeadModalOpen={isAddLeadModalOpen}
      />
			<AddLeadButton
				handleOnClickAddLeadButton={() =>
					toggleAddLeadModalOpen(!isAddLeadModalOpen)
				}
			/>
			<LeadsTable leadsList={leadsList} />
		</div>
	);
};
