import React from "react";
import { Modal, Form, Button } from "semantic-ui-react";

import "./AddLeadModal.css";

export default class AddLeadModal extends React.Component {
	state = { first_name: '', last_name: '', email: '', mobile: '', location_type: '', location_string: '' }

  handleChange = (e, { name, value }) => this.setState({ [name]: value })

  render() {
		const { isAddLeadModalOpen, toggleAddLeadModalOpen } = this.props;
    const { first_name, last_name, email, mobile, location_type, location_string } = this.state;
		return (
			<Modal
				onClose={() => toggleAddLeadModalOpen(false)}
				onOpen={() => toggleAddLeadModalOpen(true)}
				open={isAddLeadModalOpen}
			>
				<Modal.Header>Add Lead</Modal.Header>
				<Modal.Content>
					<Form onSubmit={this.handleSubmit} class="add_lead_form">
						<Form.Group>
							<div class="formContainer">
								<div class="formRow">
									<div class="formField">
										<label class="formLable">First Name</label>
										<Form.Input
											placeholder="First Name"
											name="first_name"
											value={first_name}
											onChange={this.handleChange}
										/>
									</div>
									<div class="formField">
										<label class="formLable">Last Name</label>
										<Form.Input
											placeholder="Last Name"
											name="last_name"
											value={last_name}
											onChange={this.handleChange}
										/>
									</div>
								</div>
								<div class="formRow">
									<div class="formField">
										<label class="formLable">Email Address</label>
										<Form.Input
											placeholder="Email Address"
											name="email"
											value={email}
											onChange={this.handleChange}
										/>
									</div>
									<div class="formField">
										<label class="formLable">Mobile</label>
										<Form.Input
											placeholder="Mobile"
											name="mobile"
											value={mobile}
											onChange={this.handleChange}
										/>
									</div>
								</div>
                <div class="formRow">
									<div class="formField">
										<label class="formLable">Location Type</label>
										<Form.Input
											placeholder="Location Type"
											name="location_type"
											value={location_type}
											onChange={this.handleChange}
										/>
									</div>
									<div class="formField">
										<label class="formLable">Location String</label>
										<Form.Input
											placeholder="Location String"
											name="location_string"
											value={location_string}
											onChange={this.handleChange}
										/>
									</div>
								</div>
                <div class="formRow">
                  <Form.Button content='Close' />
                  <Form.Button content='Save' class="add_lead_btn"/>
                </div>
							</div>
						</Form.Group>
					</Form>
				</Modal.Content>
			</Modal>
		);
	}
}
