import React, { useState, useEffect } from "react";
import renderIf from 'render-if'

import { AddLeadButton } from './components/Buttons'
import LeadsTable from './components/LeadsTable'
import AddLeadModal from './components/AddLeadModal'

import { fetchLeadsList } from './HomeScreenService'

import './HomeScreenStyles.css'

export const HomeScreenView = () => {
  const [leadsList, setLeadsList] = useState([])
  const [isAddLeadModalOpen, toggleAddLeadModalOpen] = useState(false)

  useEffect(() => {
		fetchLeadsList()
			.then((data) => setLeadsList(data))
			.catch((error) => console.log("Error from fetchLeadsList", error));
	}, []);


  // const renderIfAddLeadModalOpen = renderIf(toggleAddLeadModalOpen)
  return (
		<div className="mainContainer">
      <AddLeadModal
        open={isAddLeadModalOpen}
      />
			<AddLeadButton
				handleOnClickAddLeadButton={() =>
					toggleAddLeadModalOpen(!isAddLeadModalOpen)
				}
			/>
			<LeadsTable leadsList={leadsList} />
		</div>
	);
};
